import { configureStore, createSlice } from "@reduxjs/toolkit";

const initialStateItem= {
        items:[]
}

const addItem = createSlice({
    name:'addToCart',
    initialState:initialStateItem,
    reducers:{
        addToCart(state , action){
            // console.log(action.payload);
            // const e=state.items.findIndex(action.payload);
            const e=false;//console.log(e);
            if(e)
            {
                console.log('*');
                state.items[e].amount++;
            }
            else
            {
                state.items=[...state.items , action.payload];
                // console.log(state.items);
            }
        }
    }

})

export const addActions = addItem.actions;

const store = configureStore({
    reducer:{addItem:addItem.reducer}
})

export default store;
