import classes from './CartItem.module.css';
import { useDispatch , useSelector } from 'react-redux'
import { removeActions } from '../../store';

const CartItem = (props) => {
  const dispatch = useDispatch();

  // const items = useSelector((state)=>state.addItem.items);

  const { name:title, amount:quantity, price } = props.item;
  const total = quantity*price;
  // const title=useSelector()
  // console.log(title);
  const eventClickedHandler = () =>{
    // console.log(title);
    dispatch(removeActions.removeFromCart({title}));
  }
  return (
    <li className={classes.item}>
      <header>
        <h3>{title}</h3>
        <div className={classes.price}>
          ${total}
          <span className={classes.itemprice}>(${price}/item)</span>
        </div>
      </header>
      <div className={classes.details}>
        <div className={classes.quantity}>
          x <span>{quantity}</span>
        </div>
        <div className={classes.actions}>
          <button
          onClick={eventClickedHandler}>-</button>
          <button >+</button>
        </div>
      </div>
    </li>
  );
};

export default CartItem;
