import classes from './CartButton.module.css';
import { useSelector } from 'react-redux';

const CartButton = (props) => {
  const items =useSelector((state) => state.changItem.items)
  const r=0;
  items.reduce(item=> r+item.amount);

  return (
    <button className={classes.button}>
      <span>My Cart</span>
      <span className={classes.badge}>{r}</span>
    </button>
  );
};

export default CartButton;
