}, [items , dispatch])


  return (
    <>
    {show && <Notification 
    status={notification.status}
    title={notification.title}
    message={notification.message}
     />}
    <Layout>
      <Cart />
      <Products />
    </Layout>
    </>
  );
}

export default App;
